import React, { Component } from 'react';

// HEADER
const Header = (props) => {
  return(
    <header>
      <h1>To Do List</h1>
      <span>{props.tasks}</span>
    </header>
  );
}

// TASK LIST
const Task = (props) => {
  return(
    <div className="task-item">
      <span className="task-delete">&#10006;</span>
      <span className="task-text">{props.task}</span>
    </div>
  );
}

// ADD NEW TASK FORM
const AddTaskForm = () => {
  return(
    <footer>
      <form>
        <input
          className="input"
        />
        <input
          className="button"
          type="button"
          value="Add Task"
        />
      </form>
    </footer>
  );
}

class App extends Component {

  state = {
    tasks: [
      {
        task: "Laundry",
        status: false,
        id: 1
      },
      {
        task: "Dishes",
        status: true,
        id: 2
      },
      {
        task: "Vacuum",
        status: false,
        id: 3
      }
    ]
  }

  render() {
    return (
      <div className="wrapper">

        <Header
          tasks={this.state.tasks}
        />

        <div className="task-wrapper">
        {this.state.tasks.map( (task, index) =>
          <Task
            task={task.task}
            status={task.status}
            id={task.id}
            key={task.id.toString()}
            index={index}
          />
        )}
        </div>

        <AddTaskForm />

      </div>

    );
  }
}
export default App;
