import React, { Component } from 'react';

// HEADER
const Header = (props) => {
  return(
    <header>
      <h1>To Do List</h1>
      <span>{props.tasks.length}</span>
    </header>
  );
}

// TASK LIST
const Task = (props) => {
  return(
    <div className="task-item">
      <span className="task-delete">&#10006;</span>
      <span className="task-text">{props.task}</span>
    </div>
  );
}

// ADD NEW TASK FORM
class AddTaskForm extends Component {

  state = {
    value: ''
  };

  handleValueChange = (e) => {
    this.setState = ({value: e.state.value});
  }

  handleSubmit = (e) => {
    e.preventDefault();
    this.props.addTask(this.state.value);
    this.setState({
      value: ""
    });
  }

  render() {
    return(
      <footer>
        <form onSubmit={this.handleSubmit}>
          <input
            className="input"
            type="text"
            onChange={this.handleValueChange}
            placeholder="Enter a Task"
          />
          <input
            className="button"
            type="submit"
            value="Add Task"
          />
        </form>
      </footer>
    );
  }
}

class App extends Component {

  state = {
    tasks: [
      {
        task: "Laundry",
        status: false,
        id: 1
      },
      {
        task: "Dishes",
        status: true,
        id: 2
      },
      {
        task: "Vacuum",
        status: false,
        id: 3
      },
      {
        task: "Code",
        status: true,
        id: 4
      }
    ]
  }

  // task id counter
  prevTaskId = 4;

  handleAddTask = (task) => {
    this.setState( prevState => {
      return {
        task: [
          ...this.state.tasks,
          {
            task: task,
            status: false,
            id: this.prevTaskId += 1
          }
        ]
      };
    });
  }

  render() {
    return (
      <div className="wrapper">

        <Header
          tasks={this.state.tasks}
        />

        <div className="task-wrapper">
        {this.state.tasks.map( (task, index) =>
          <Task
            task={task.task}
            status={task.status}
            id={task.id}
            key={task.id.toString()}
            index={index}
          />
        )}
        </div>

        <AddTaskForm
          addTask={this.handleAddTask}
        />

      </div>

    );
  }
}
export default App;
